import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { addReview, getReviews, getAverageRating, getMovieDetails } from '../services/movieService';

const MovieDetails = () => {
  const { id } = useParams(); // Obtém o ID do filme da URL
  const [movie, setMovie] = useState(null);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const [reviews, setReviews] = useState([]);
  const [averageRating, setAverageRating] = useState(0);
  const [error, setError] = useState('');

  useEffect(() => {
    // Função assíncrona para buscar detalhes do filme
    const fetchMovieDetails = async () => {
      try {
        const movieData = await getMovieDetails(id);
        setMovie(movieData);
        const movieReviews = await getReviews(id);
        setReviews(movieReviews);
        const avgRating = await getAverageRating(id);
        setAverageRating(avgRating);
      } catch (err) {
        setError('Erro ao carregar os dados do filme.');
      }
    };

    fetchMovieDetails();
  }, [id]);

  const handleSubmitReview = async () => {
    if (!rating || !comment) {
      setError('Avaliação e comentário são obrigatórios!');
      return;
    }

    const token = localStorage.getItem('token');
    if (token) {
      try {
        const response = await addReview(id, rating, comment, token);
        setReviews((prevReviews) => [...prevReviews, response.review]);
        setAverageRating(response.averageRating);
        setRating(0);
        setComment('');
        setError('');
      } catch (err) {
        setError('Erro ao submeter a avaliação.');
      }
    } else {
      setError('Você precisa estar logado para submeter uma avaliação.');
    }
  };

  if (!movie) return <p>Carregando filme...</p>;  // Mensagem de carregamento

  return (
    <div>
      <h2>{movie.Title}</h2>
      <p>{movie.Plot}</p>
      <img src={movie.Poster} alt={movie.Title} />
      
      <div>
        <h3>Avalie este filme</h3>
        <div>
          <label>Avaliação (1 a 5):</label>
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              onClick={() => setRating(star)}
              style={{ cursor: 'pointer', color: rating >= star ? 'gold' : 'gray' }}
            >
              ★
            </span>
          ))}
        </div>
        
        <textarea
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          placeholder="Deixe sua opinião"
        />
        
        <button onClick={handleSubmitReview}>Submeter Avaliação</button>
        {error && <p>{error}</p>}
      </div>

      <div>
        <h3>Média de Avaliação: {averageRating.toFixed(1)} ★</h3> {/* Exibe a média com 1 casa decimal */}
        <h4>Comentários:</h4>
        {reviews.map((review, index) => (
          <div key={index}>
            <p><strong>{review.username}</strong> - {review.date}</p>
            <p>{review.comment}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MovieDetails;
